Object.assign(window.search, {"doc_urls":["index.html#北京大运河博物馆","index.html#历史沿革","index.html#成立背景","index.html#建设历程","index.html#方位布局","index.html#场馆设计","index.html#场馆特点","index.html#陈列展览","index.html#综述","index.html#基本陈列","index.html#专题展览","index.html#临时展览","index.html#重要馆藏","index.html#文化活动","index.html#标志标识","index.html#名称由来"],"index":{"documentStore":{"docInfo":{"0":{"body":0,"breadcrumbs":0,"title":0},"1":{"body":0,"breadcrumbs":0,"title":0},"10":{"body":0,"breadcrumbs":0,"title":0},"11":{"body":0,"breadcrumbs":0,"title":0},"12":{"body":0,"breadcrumbs":0,"title":0},"13":{"body":0,"breadcrumbs":0,"title":0},"14":{"body":0,"breadcrumbs":0,"title":0},"15":{"body":0,"breadcrumbs":0,"title":0},"2":{"body":0,"breadcrumbs":0,"title":0},"3":{"body":3,"breadcrumbs":0,"title":0},"4":{"body":0,"breadcrumbs":0,"title":0},"5":{"body":0,"breadcrumbs":0,"title":0},"6":{"body":0,"breadcrumbs":0,"title":0},"7":{"body":0,"breadcrumbs":0,"title":0},"8":{"body":0,"breadcrumbs":0,"title":0},"9":{"body":0,"breadcrumbs":0,"title":0}},"docs":{"0":{"body":"北京大运河博物馆，位于北京市通州区绿心路1号院5号楼，是一座智慧型综合性博物馆，集收藏保管、开放式展陈、科学研究、社会教育、文保修复等功能于一体，总建筑面积约9.97万平方米。 北京大运河博物馆于2019年10月28日正式动工。2023年12月27日，北京大运河博物馆正式对公众开放。北京大运河博物馆由共享大厅、展陈大楼（主楼）和休闲水街共同组成。博物馆建设设计理念源于古运河图景中的船、帆、水三个元素，以运河为线索，将历史文化融入建筑之中。共享大厅屋顶造型取自“船”，展陈大楼（主楼）屋顶造型取自“帆”。","breadcrumbs":"概况简介 » 北京大运河博物馆","id":"0","title":"北京大运河博物馆"},"1":{"body":"","breadcrumbs":"概况简介 » 历史沿革","id":"1","title":"历史沿革"},"10":{"body":"北京大运河博物馆专题展览“北京大运河揽胜图卷”“不止运河——沉浸式体验展”。 北京是京津冀区域中心城市。在漫长的历史岁月中，随着北京城市功能的演进，燕赵大地上形成了以北京为中心的城市格局。从周边城市拱卫京师、服务首都，到协同发展，京津冀协同发展国家战略是中国共产党人在总结历史经验的基础上作出的伟大创举。","breadcrumbs":"概况简介 » 专题展览","id":"10","title":"专题展览"},"11":{"body":"北京大运河博物馆临时展览有“北京画院藏花鸟画精品展”“北京城市副中心建设发展影像展”；开放展示“石刻园林展示区”“博物馆防震科普展”“公共艺术平台”“三星堆与金沙展”等。 2024年6月27日起，“探秘古蜀文明之三星堆”展览将在北京大运河博物馆（首都博物馆东馆）展出，一批古蜀文明相关文物展出。","breadcrumbs":"概况简介 » 临时展览","id":"11","title":"临时展览"},"12":{"body":"北京大运河博物馆征集文物展品的形态包括但不限于文物、物证、标本、出版物、复仿制品、拓片，以及照片、视频、录音、3D扫描等影音文件。","breadcrumbs":"概况简介 » 重要馆藏","id":"12","title":"重要馆藏"},"13":{"body":"","breadcrumbs":"概况简介 » 文化活动","id":"13","title":"文化活动"},"14":{"body":"北京大运河博物馆（首都博物馆东馆）的标志以“千年大运河，流动的文化”为核心设计理念。大运河是一条承载着密集中国文化基因的大动脉，标志以“京运龙印、风帆舟楫、绵延水纹”为核心图形元素，多义共生，将中国传统文化要素中的大运河主题用生动、巧妙的视觉语言进行融合创新设计，擦亮大运河“文化名片”，打造博物馆新文化地标品牌，加强千年运河文化品牌的形象传播，向世界讲好大运河故事。","breadcrumbs":"概况简介 » 标志标识","id":"14","title":"标志标识"},"15":{"body":"大运河博物馆原先的项目名称为“首都博物馆东馆”，北京市通州区政协委员在调研视察的过程中发现其具有重要的文化特征，希望能传承好、利用好大运河文化，为后人讲述更多精彩的故事。因此多次建议后将博物馆命名为“大运河博物馆”。","breadcrumbs":"概况简介 » 名称由来","id":"15","title":"名称由来"},"2":{"body":"北京市深入挖掘以大运河为核心的历史文化资源，先后在白浮泉、万寿寺、玉河通惠河、西板桥、清固伦和敬公主园寝、路县故城、大运河故道、小圣庙、善人桥9处文化遗产取得重要考古成果，为进一步丰富大运河文化带与北京老城的内在联系与文化内涵，将从大运河与北京城市发展的关系角度，诠释和弘扬大运河文化，修建北京大运河博物馆。","breadcrumbs":"概况简介 » 成立背景","id":"2","title":"成立背景"},"3":{"body":"2019年10月28日，北京大运河博物馆正式动工。 2023年2月，完成所有建设工程，计划于年底开放。 2023年7月，北京大运河博物馆获得正式命名。 2023年8月，由北京城市图书馆、北京艺术中心和北京大运河博物馆共同构成的北京城市副中心三大文化建筑9月底具备竣工验收条件。 2023年11月，北京大运河博物馆（首都博物馆东馆）顺利通过竣工验收，进入开放倒计时。 2023年12月27日下午，北京大运河博物馆（首博东馆）正式对公众开放。","breadcrumbs":"概况简介 » 建设历程","id":"3","title":"建设历程"},"4":{"body":"","breadcrumbs":"概况简介 » 方位布局","id":"4","title":"方位布局"},"5":{"body":"北京大运河博物馆总建筑面积约9.97万平方米，由共享大厅、展陈大楼（主楼）和休闲水街共同组成。博物馆建设设计理念源于古运河图景中的船、帆、水三个元素，以运河为线索，将历史文化融入建筑之中。共享大厅屋顶造型取自“船”，展陈大楼（主楼）屋顶造型取自“帆”。两座建筑之间的休闲水街，与展陈大楼（主楼）立面的街巷路口、篷架、门楼等元素相结合，形成一处类似于运河驳岸码头的场景。 北京大运河博物馆在空间和建筑形态上，共享大厅北侧与草坡自然衔接，展陈大楼（主楼）南侧向城市绿心森林公园自然过渡，室内室外自然和谐；为保障文物安全，展陈大楼（主楼）地下一层下方设置1.8万平方米的隔震层，是中国已建成的隔震博物馆中隔震区域投影面积最大的；共享大厅、展陈大楼（主楼）和休闲水街相对独立又彼此联系，比单体式博物馆建筑空间更开放、更自由，能够实现更灵活的博物馆开放模式，满足当代博物馆多元化服务的需求。","breadcrumbs":"概况简介 » 场馆设计","id":"5","title":"场馆设计"},"6":{"body":"北京大运河博物馆为智慧型综合性博物馆，集收藏保管、开放式展陈、科学研究、社会教育、文保修复等功能于一体，将成为立足城市副中心、辐射京津冀的爱国主义教育基地、提升文化软实力的重要阵地和展示城市发展的生动窗口，为参观者带来文化熏陶与情感共鸣，进一步满足市民的文化学习需求。","breadcrumbs":"概况简介 » 场馆特点","id":"6","title":"场馆特点"},"7":{"body":"","breadcrumbs":"概况简介 » 陈列展览","id":"7","title":"陈列展览"},"8":{"body":"北京大运河博物馆围绕大运河与北京相辅相成的密切关系，以人、水、城为内容主线，从人文地理视角、生态文明高度展示北京城市发展历史、建设成就，推出一系列展览。以北京城市总体规划为指导，配合北京城市战略定位，与首都博物馆从历史文化的角度共同诠释“都”与“城”的关系。展陈体系由基本陈列、专题展览、临时展览、开放展示四大类型构成。","breadcrumbs":"概况简介 » 综述","id":"8","title":"综述"},"9":{"body":"北京大运河博物馆基本陈列有“京华通惠运河永济——北京与大运河历史文化陈列”。 大运河由京杭大运河、隋唐大运河、浙东运河三部分构成，联通海河、黄河、淮河、长江、钱塘江五大水系，跨越2500多年、奔流近3200公里，是世界上距离最长、规模最大的运河。大运河促进了南北交融、中外交流，维系着国家统一、民族团结。大运河向千年古都源源不断输送养分，推动北京从北疆重镇迈向国家首都，塑造了北京城市文化的集大成性，首善之区多元荟萃成果又藉由大运河反哺全国，日益巩固北京作为大国首都的地位。","breadcrumbs":"概况简介 » 基本陈列","id":"9","title":"基本陈列"}},"length":16,"save":true},"fields":["title","body","breadcrumbs"],"index":{"body":{"root":{"2":{"0":{"1":{"9":{"df":0,"docs":{},"年":{"1":{"0":{"df":0,"docs":{},"月":{"2":{"8":{"df":1,"docs":{"3":{"tf":1.0}}},"df":0,"docs":{}},"df":0,"docs":{}}},"df":0,"docs":{}},"df":0,"docs":{}}},"df":0,"docs":{}},"2":{"3":{"df":0,"docs":{},"年":{"2":{"df":1,"docs":{"3":{"tf":1.0}}},"7":{"df":1,"docs":{"3":{"tf":1.0}}},"df":0,"docs":{}}},"df":0,"docs":{}},"df":0,"docs":{}},"df":0,"docs":{}},"df":0,"docs":{}}},"breadcrumbs":{"root":{"2":{"0":{"1":{"9":{"df":0,"docs":{},"年":{"1":{"0":{"df":0,"docs":{},"月":{"2":{"8":{"df":1,"docs":{"3":{"tf":1.0}}},"df":0,"docs":{}},"df":0,"docs":{}}},"df":0,"docs":{}},"df":0,"docs":{}}},"df":0,"docs":{}},"2":{"3":{"df":0,"docs":{},"年":{"2":{"df":1,"docs":{"3":{"tf":1.0}}},"7":{"df":1,"docs":{"3":{"tf":1.0}}},"df":0,"docs":{}}},"df":0,"docs":{}},"df":0,"docs":{}},"df":0,"docs":{}},"df":0,"docs":{}}},"title":{"root":{"df":0,"docs":{}}}},"lang":"English","pipeline":["trimmer","stopWordFilter","stemmer"],"ref":"id","version":"0.9.5"},"results_options":{"limit_results":30,"teaser_word_count":30},"search_options":{"bool":"OR","expand":true,"fields":{"body":{"boost":1},"breadcrumbs":{"boost":1},"title":{"boost":2}}}});