Object.assign(window.search, {"doc_urls":["index.html#国家植物园","index.html#玫瑰和月季的区别","index.html#形态特征","index.html#生长习性","index.html#文化与用途","index.html#牡丹和芍药的区别","index.html#形态特征-1","index.html#生长习性-1","index.html#文化象征"],"index":{"documentStore":{"docInfo":{"0":{"body":0,"breadcrumbs":0,"title":0},"1":{"body":0,"breadcrumbs":0,"title":0},"2":{"body":3,"breadcrumbs":0,"title":0},"3":{"body":0,"breadcrumbs":0,"title":0},"4":{"body":0,"breadcrumbs":0,"title":0},"5":{"body":0,"breadcrumbs":0,"title":0},"6":{"body":2,"breadcrumbs":0,"title":0},"7":{"body":0,"breadcrumbs":0,"title":0},"8":{"body":0,"breadcrumbs":0,"title":0}},"docs":{"0":{"body":"","breadcrumbs":"概况简介 » 国家植物园","id":"0","title":"国家植物园"},"1":{"body":"玫瑰和月季同属蔷薇科蔷薇属，外形相似。","breadcrumbs":"概况简介 » 玫瑰和月季的区别","id":"1","title":"玫瑰和月季的区别"},"2":{"body":"刺：月季的刺少或无刺，刺较大，一般每节三四个，易掰下；玫瑰的刺密且小，是茎干木质化的一部分，难掰动。 叶：月季叶子光亮，小叶3-5片，叶纹不明显；玫瑰叶子不光滑，小叶可达7-8片，有明显纹理，背面有柔毛。 花：月季花朵大，花瓣重叠，轮生多，颜色渐变和多色的情况常见；玫瑰花朵较小，花瓣一般三轮，多开在一个平面，花色相对较少。","breadcrumbs":"概况简介 » 形态特征","id":"2","title":"形态特征"},"3":{"body":"花期：月季若养护得当，季季开花，也叫月月红、长春花；玫瑰一般只在夏季开一次花。 适应性：月季适应性强，生命力顽强，现代月季品种抗病性好；玫瑰对生长环境要求高，易受病虫害侵袭。","breadcrumbs":"概况简介 » 生长习性","id":"3","title":"生长习性"},"4":{"body":"文化寓意：玫瑰是爱情象征，在西方文化中常出现；月季在中国被誉为“花中皇后”，象征富贵、吉祥与坚韧。 用途：玫瑰常被用于提炼香精、制作花茶、花酱及化妆品等；月季主要用于观赏，可布置园林、花坛，也可作切花。","breadcrumbs":"概况简介 » 文化与用途","id":"4","title":"文化与用途"},"5":{"body":"","breadcrumbs":"概况简介 » 牡丹和芍药的区别","id":"5","title":"牡丹和芍药的区别"},"6":{"body":"茎干：牡丹是木本植物，茎干木质化，冬天落叶后地上部分枝干保留；芍药是草本植物，冬天落叶后地上部分枯死，次年春天从土里重新冒出茎叶。 叶片：牡丹叶片通常为二回三出羽状复叶，宽而大，先端常分裂，上表面绿色略泛黄，下表面有白粉；芍药叶片狭长，先端不分裂，正反面均为浓绿色，较有光泽，下表面无白粉。 花朵：牡丹花多独朵顶生，花径一般20厘米左右，颜色丰富；芍药花一朵或数朵顶生、腋生，花径多在15厘米左右，颜色相对较少。 果实：牡丹果实为蓇葖果，呈五角形，内有5-15枚大粒种子，成熟时橘黄色，老时黑褐色；芍药果实是聚合蓇葖果，2-8枚离生，呈纺锤形、椭圆形等，种子黑色或黑褐色。","breadcrumbs":"概况简介 » 形态特征","id":"6","title":"形态特征"},"7":{"body":"花期：牡丹一般在4月下旬开花，芍药在5月上中旬开花，牡丹比芍药早半个月左右。 土壤要求：牡丹对土壤要求较高，需排水良好、肥沃的土壤；芍药对土壤要求相对不严格。","breadcrumbs":"概况简介 » 生长习性","id":"7","title":"生长习性"},"8":{"body":"牡丹：被称为“花王”，象征着富贵、繁荣、昌盛，是中国传统文化中的重要符号，常出现在绘画、诗词、建筑装饰等方面。 芍药：被誉为“花相”，象征着友谊、爱情，常被用于表达离别之情或赠送友人，在古代诗词中也有大量的描写。","breadcrumbs":"概况简介 » 文化象征","id":"8","title":"文化象征"}},"length":9,"save":true},"fields":["title","body","breadcrumbs"],"index":{"body":{"root":{"3":{"df":1,"docs":{"2":{"tf":1.0}}},"5":{"df":1,"docs":{"6":{"tf":1.0}},"片":{"df":0,"docs":{},"，":{"df":0,"docs":{},"叶":{"df":0,"docs":{},"纹":{"df":0,"docs":{},"不":{"df":0,"docs":{},"明":{"df":0,"docs":{},"显":{"df":0,"docs":{},"；":{"df":0,"docs":{},"玫":{"df":0,"docs":{},"瑰":{"df":0,"docs":{},"叶":{"df":0,"docs":{},"子":{"df":0,"docs":{},"不":{"df":0,"docs":{},"光":{"df":0,"docs":{},"滑":{"df":0,"docs":{},"，":{"df":0,"docs":{},"小":{"df":0,"docs":{},"叶":{"df":0,"docs":{},"可":{"df":0,"docs":{},"达":{"7":{"df":1,"docs":{"2":{"tf":1.0}}},"df":0,"docs":{}}}}}}}}}}}}}}}}}}}}}},"8":{"df":2,"docs":{"2":{"tf":1.0},"6":{"tf":1.0}}},"df":0,"docs":{}}},"breadcrumbs":{"root":{"3":{"df":1,"docs":{"2":{"tf":1.0}}},"5":{"df":1,"docs":{"6":{"tf":1.0}},"片":{"df":0,"docs":{},"，":{"df":0,"docs":{},"叶":{"df":0,"docs":{},"纹":{"df":0,"docs":{},"不":{"df":0,"docs":{},"明":{"df":0,"docs":{},"显":{"df":0,"docs":{},"；":{"df":0,"docs":{},"玫":{"df":0,"docs":{},"瑰":{"df":0,"docs":{},"叶":{"df":0,"docs":{},"子":{"df":0,"docs":{},"不":{"df":0,"docs":{},"光":{"df":0,"docs":{},"滑":{"df":0,"docs":{},"，":{"df":0,"docs":{},"小":{"df":0,"docs":{},"叶":{"df":0,"docs":{},"可":{"df":0,"docs":{},"达":{"7":{"df":1,"docs":{"2":{"tf":1.0}}},"df":0,"docs":{}}}}}}}}}}}}}}}}}}}}}},"8":{"df":2,"docs":{"2":{"tf":1.0},"6":{"tf":1.0}}},"df":0,"docs":{}}},"title":{"root":{"df":0,"docs":{}}}},"lang":"English","pipeline":["trimmer","stopWordFilter","stemmer"],"ref":"id","version":"0.9.5"},"results_options":{"limit_results":30,"teaser_word_count":30},"search_options":{"bool":"OR","expand":true,"fields":{"body":{"boost":1},"breadcrumbs":{"boost":1},"title":{"boost":2}}}});